import React from 'react'
import './App.css';

function App() {
  const handleChange = (e) => {
    this.setState({selectedValue: e.target.value})
    var x,y,z, D, k;
    x = document.getElementById('length').value;
    y = document.getElementById('width').value;
    z = document.getElementById('height').value;
    D = document.getElementById('measurement').value;
    if(D === "centi"){
      k = x*y*z;
     document.getElementById('vol').value = k
    }
  }



  return (
    <div className="App">
      <label> Unit of measurement</label>
      <select id = "measurement" onChange={(e) => handleChange(e)} >
        <option value= "centi">cm</option>
        <option value = "mili">mm</option>
        <option value = "meter">m</option>
      </select>
      <br></br>
    <label>Length </label>
    <input type= "number" id = "length" name = "" onKeyUp = {(e) => handleChange(e)} />
    <br></br>
    <label>Width </label>
    <input type= "number" id = "width" name = "" onKeyUp = {(e) => handleChange(e)}/>
    <br></br>
    <label>Height </label>
    <input type= "number" id = "height" name = "" onKeyUp = {(e) => handleChange(e)}/>
    <br></br>
    <label> Volume (Cubic Meter) </label>
    <input type= "number" id = "vol" name = "" readOnly = {true}/>
    <br></br>
    </div>
  );
}

export default App;
