import React, { useEffect, useState } from 'react'
import './App.css';

function App() {
  /**const handleChange = (e) => {
    this.setState({selectedValue: e.target.value})
    var x,y,z, D, k;
    x = document.getElementById('length').value;
    console.log(x);
    y = document.getElementById('width').value;
    z = document.getElementById('height').value;
    D = document.getElementById('measurement').value; //can be event.target.value
    if(D === "centi"){
      k = x*y*z;
     document.getElementById('vol').value = k
    }
  }
  **/
  const [length, setLength] = useState(0);
  const [width, setWidth] = useState(0);
  const [height, setHeight] = useState(0);
  const [cubicVolume, setCubicVolume] = useState(length * width * height);
  const [OptionValue, setOptionValue] = useState("");


  const handleSelection = (e) => {

    console.log(e.target.value);
    var a = e.target.value;
    setOptionValue(a);
    console.log("-------- a " + a);
    console.log(OptionValue);

  }
  const reset = () => {
    setHeight(0);
    setLength(0);
    setWidth(0);
    setCubicVolume(0);
    setOptionValue("");

  }

  const styling = {
    width: '30%',
    padding: '12px' ,
    margin: '8px' ,
    boxSize: 'border-box',
    align: 'center'
  }

  const getVolume = () => {
   // setCubicVolume(length * width * height);

    if (JSON.stringify(OptionValue) === "mili") {
      //console.log(OptionValue)
      setCubicVolume(0.001 * 0.001 * 0.001 * length * width * height);  // check for the previous option value
      console.log(cubicVolume);

    }
    if (JSON.stringify(OptionValue) === "centi") {
      //console.log(OptionValue)
      setCubicVolume(0.01 * 0.01 * 0.01 * length * width * height);
      console.log(cubicVolume);

    }
    if (JSON.stringify(OptionValue) === "meter") {
      //console.log(OptionValue)
      setCubicVolume(length * width * height);
      console.log(cubicVolume);

    }
  }




  return (
    <div className="App">
      <label>Unit of measurement</label>
      <select style = {styling} onSelect = {handleSelection} >
      <option value = "" disabled>Select your value </option>
        <option value = "mili">mm</option>
        <option value = "centi">cm</option>
        <option value = "meter">m</option>
      </select>

      <br></br>
      <label>Length </label>
      <input type="number" style = {styling} value={length} onChange={e => setLength(+e.target.value)} />
      <br></br>
      <label>Width </label>
      <input type="number" style = {styling} value={width} onChange={e => setWidth(+e.target.value)} />
      <br></br>
      <label>Height </label>
      <input type="number" style = {styling} value={height} onChange={e => setHeight(+e.target.value)} />
      <br>
      </br>
      <label> Volume (Cubic Meter) </label>
      <input type="number" style = {styling} value={cubicVolume} readOnly={true} />
      <br>
      </br>
      <button style = {styling} onClick={reset}>Reset</button>
      <button style = {styling} onClick={getVolume}>Calculate Volume</button>

    </div>
  );
}

export default App;
