import React, { useState } from 'react'
import './App.css';

function App() {
  /**const handleChange = (e) => {
    this.setState({selectedValue: e.target.value})
    var x,y,z, D, k;
    x = document.getElementById('length').value;
    console.log(x);
    y = document.getElementById('width').value;
    z = document.getElementById('height').value;
    D = document.getElementById('measurement').value; //can be event.target.value
    if(D === "centi"){
      k = x*y*z;
     document.getElementById('vol').value = k
    }
  }
  **/
 const [length, setLength] = useState("");
 const [width, setWidth] = useState("");
 const [height, setHeight] = useState("");
 const [cubicVolume, setCubicVolume] = useState(length*width*height);
 const [OptionValue, setOptionValue] = useState("");  
 const handleSelection = (e) => {
   console.log(e.target.value);
   setOptionValue(prevOptionValue => prevOptionValue + e.target.value);


 }




  return (
    <div className="App">
      <label> Unit of measurement</label>
      <select id = "measurement"   onChange = {handleSelection} >
        <option value="" disabled selected>Select your Unit of measurement</option>
        <option value= "centi">cm</option>
        <option value = "mili">mm</option>
        <option value = "meter">m</option>
      </select>
      <br></br>
    <label>Length </label>
    <input type= "number" value = {length} onChange = {e => setLength(+e.target.value)}  />
    <br></br>
    <label>Width </label>
    <input type= "number" value = {width} onChange = {e => setWidth(+e.target.value)}/>
    <br></br>
    <label>Height </label>
    <input type= "number" value = {height}  onChange = {e => setHeight(+e.target.value)}/>
    <br></br>
    <label> Volume (Cubic Meter) </label>
    <input type= "number" value = {cubicVolume}  readOnly = {true}/>
    <br></br>
    </div>
  );
}

export default App;
